$explorerprocesses = @(Get-WmiObject -ComputerName '172.17.24.149' -Query "Select * FROM Win32_Process WHERE Name='explorer.exe'" -ErrorAction SilentlyContinue)if ($explorerprocesses.Count -eq 0){    "No explorer process found / Nobody interactively logged on"} else {    foreach ($i in $explorerprocesses)    {        $Username = $i.GetOwner().User        $Domain = $i.GetOwner().Domain        $Domain + "\" + $Username + " logged on since: " + ($i.ConvertToDateTime($i.CreationDate))    }}